00230         private static void IncrementFrameId()
00231         {
00232             _prevSubFrameId = _currSubFrameId;
00233             _currSubFrameId++;
00234 
00235             if (_currSubFrameId >= TickEngineSettings.sendEveryXTick)
00236             {
00237                 _currSubFrameId = 0;
00238                 _prevFrameId = _currFrameId;
00239 
00240                 _currFrameId++;
00241                 if (_currFrameId >= TickEngineSettings.frameCount)
00242                     _currFrameId = 0;
00243             }
00244 
00245             NetMasterCallbacks.OnIncrementFrameCallbacks(_currFrameId, _currSubFrameId, _prevFrameId, _prevSubFrameId);
00246 
00247             if (_currSubFrameId == 0)
00248             {
00250                 TickManager.PreSnapshot(_currFrameId);
00251 
00252                 // Snapshot NetObjects
00253                 NetObject.NetObjDictsLocked = true;
00254                 foreach (var no in NetObject.activeUncontrolledNetObjs.Values)
00255                     no.OnSnapshot(_currFrameId);
00256                 NetObject.NetObjDictsLocked = false;
00257 
00258                 // Snapshot Others
00259                 NetMasterCallbacks.OnSnapshotCallbacks(_currFrameId);
00260 
00261                 TickManager.PostSnapshot(_currFrameId);
00262 
00263                 lastSentTickTime = Time.fixedTime;
00264             }
00265         }
