00370         private static void SerializeNetObjDict(NonAllocDictionary<int, NetObject> dict, byte[] buffer, ref int bitposition, ref SerializationFlags flags, SerializationFlags writeFlags)
00371         {
00372             foreach (var no in dict.Values)
00373             {
00374 
00376                 int holdStartPos = bitposition;
00377 
00379                 buffer.WritePackedBytes((uint)no.ViewID, ref bitposition, 32);
00380 
00382                 int holdHasDataPos = bitposition;
00383                 buffer.WriteBool(true, ref bitposition);
00384 
00386                 int holdDataSizePos = bitposition;
00387                 bitposition += BITS_FOR_NETOBJ_SIZE;
00388 
00389                 var objflags = no.OnNetSerialize(_currFrameId, buffer, ref bitposition, writeFlags);
00390 
00392                 if (objflags == SerializationFlags.None)
00393                 {
00395                     if (no.SkipWhenEmpty)
00396                     {
00397                         bitposition = holdStartPos;
00398                     }
00399                     else
00400                     {
00401                         bitposition = holdHasDataPos;
00402                         buffer.WriteBool(false, ref bitposition);
00403                     }
00404                 }
00405                 else
00406                 {
00408                     flags |= objflags;
00409                     int bitcount = bitposition - holdDataSizePos;
00410                     buffer.Write((uint)bitcount, ref holdDataSizePos, BITS_FOR_NETOBJ_SIZE);
00411                 }
00412 
00413                 //Debug.Log(objflags + " / flg: " + (onNetSerialize[i]  as Component).name + " " + flags);
00414             }
00415         }
