00421         public static void ReceiveMessage(object conn, int connId, byte[] buffer)
00422         {
00423             int frameCount = TickEngineSettings.frameCount;
00424             int bitposition = 0;
00425 
00426 
00428             int originFrameId = (int)buffer.Read(ref bitposition, TickEngineSettings.frameCountBits);
00429 
00430             //Debug.Log("in " + originFrameId);
00431 
00432 #if DEVELOPMENT_BUILD || UNITY_EDITOR
00433             if (originFrameId < 0 || originFrameId >= frameCount)
00434                 Debug.LogError("INVALID FRAME ID " + originFrameId + " CORRUPT STREAM LIKELY DUE TO READ/WRITE DISAGREEMENT.");
00435 #endif
00436             FrameArrival arrival;
00437             /*var offsets = */
00438             TickManager.LogIncomingFrame(connId, originFrameId, out arrival);
00439 
00441             //NetMasterCallbacks.OnPreDeserializeTickCallbacks(localFrameId, buffer, ref bitposition);
00442 
00444             while (true)
00445             {
00447                 int viewid = (int)buffer.ReadPackedBytes(ref bitposition, 32);
00448 
00449                 if (viewid == 0)
00450                     break;
00451 
00453                 bool hasData = buffer.ReadBool(ref bitposition);
00454 
00456                 if (!hasData)
00457                     continue;
00458 
00460                 int holdDataSizePos = bitposition;
00461                 int bitcount = (int)buffer.Read(ref bitposition, BITS_FOR_NETOBJ_SIZE);
00462                 int expectedBitPosition = holdDataSizePos + bitcount;
00463 
00464                 var pv = PhotonNetwork.GetPhotonView(viewid);
00465                 var netobj = pv ? pv.GetComponent<NetObject>() : null;
00466 
00468                 if (ReferenceEquals(netobj, null))
00469                 {
00470                     bitposition = expectedBitPosition;
00471                     continue;
00472                 }
00473 
00475                 if (netobj.IgnoreNonControllerUpdates)
00476                 {
00477                     int controllerActorNr = pv.ControllerActorNr;
00478                     int ownerActorNr = pv.OwnerActorNr;
00479 
00480                     if (controllerActorNr == -1)
00481                     {
00482 #if UNITY_EDITOR || DEVELOPMENT_BUILD
00483                         Debug.Log(pv.name + ":" + pv.ViewID + " OwnershipUpdate has not yet arrived from master, so owner is still unknown. Accepting update from Actor " + connId + " as temporary controller until OwnershipUpdate arrives and sets the Owner/Controller.");
00484 #endif
00485                         pv.ControllerActorNr = connId;
00486                     }
00487                     else if (controllerActorNr != connId && ownerActorNr != connId)
00488                     {
00489                         {
00490 #if UNITY_EDITOR || DEVELOPMENT_BUILD
00491                             Debug.LogError("fid: " + originFrameId + "Update from ActorId: " + connId + " for object " + netobj.name + ":" + pv.ViewID + " , but that conn does not have controller authority. Current cowner:controller is "
00492                                 + pv.OwnerActorNr + ":" + controllerActorNr);
00493 #endif
00494                             bitposition = expectedBitPosition;
00495                             continue;
00496                         }
00497                     }
00498                 }
00499 
00500 
00501 
00502                 netobj.OnDeserialize(connId, originFrameId, buffer, ref bitposition, hasData, arrival);
00503 
00504 
00505 #if UNITY_EDITOR
00506                 if (bitposition != expectedBitPosition)
00507                     Debug.LogWarning("Deserialization mismatch for object " + netobj.name + " viewId: " + netobj.ViewID + " owned by: " + netobj.photonView.OwnerActorNr +
00508                         ". Bitposition off by: " + (expectedBitPosition - bitposition) + " bits.");
00509 #endif
00511                 bitposition = expectedBitPosition;
00512 
00513             }
00514         }
