00516         public static FrameArrival CheckFrameArrival(int incomingFrame)
00517         {
00518             int delta = incomingFrame - _prevFrameId;
00519 
00520             if (delta == 0)
00521                 return FrameArrival.IsSnap;
00522 
00523             // change negative values into positive to check for wrap around.
00524             if (delta < 0)
00525                 delta += TickEngineSettings.frameCount;
00526 
00527             if (delta == 1)
00528                 return FrameArrival.IsTarget;
00529 
00530             if (delta >= TickEngineSettings.halfFrameCount)
00531                 return FrameArrival.IsLate;
00532 
00533             return FrameArrival.IsFuture;
00534 
00535         }
