00053         [MenuItem(AssistHelpers.CONVERT_TO_FOLDER + "Platform", false, AssistHelpers.CONVERTTO_PRIORITY)]
00054         public static void ConvertToPlatform()
00055         {
00056             var go = GetSelectedGameObject();
00057 
00058             if (!go)
00059                 return;
00060 
00061             if (go.transform.lossyScale != Vector3.one)
00062             {
00063                 if (go.transform.parent && go.transform.parent.lossyScale != Vector3.one)
00064                 {
00065                     Debug.LogWarning("Aborted Convert To Platform. Parent object has a scale other than " + Vector3.one + " which would distort any object that mounts to it.");
00066                     return;
00067                 }
00068 
00069                 Debug.LogWarning("Selected object as a scale other than " + Vector3.one + ". Creating a parent object for the platform and making the selected object a child.");
00070                 var par = new GameObject("Platform");
00071                 par.transform.position = go.transform.position;
00072                 par.transform.rotation = go.transform.rotation;
00073                 par.transform.parent = go.transform.parent;
00074                 go.transform.parent = par.transform;
00075 
00076                 go = par;
00077             }
00078 
00079             go.EnsureComponentExists<NetObject>(true);
00080             go.EnsureComponentExists<Mount>();
00081             var mover = go.EnsureComponentExists<SyncNodeMover>();
00082             mover.posDef.includeAxes = AxisMask.Y;
00083             mover.StartNode.Pos = new Vector3(0, -2f, 0);
00084             mover.EndNode.Pos = new Vector3(0, 2f, 0);
00085             mover.oscillatePeriod = 3;
00086 
00087             mover.rotDef.includeAxes = AxisMask.None;
00088             mover.sclDef.includeAxes = AxisMask.None;
00089 
00090             Selection.activeGameObject = go;
00091         }
