00238         public static Transform RecursiveFind(this Transform t, string name)
00239         {
00240             for (int i = 0; i < t.childCount; ++i)
00241             {
00242                 var child = t.GetChild(i);
00243                 if (/*child.name.Length == name.Length && */child.name == name)
00244                 {
00245                     return child;
00246                 }
00247                 else
00248                 {
00249                     var found = RecursiveFind(child, name);
00250                     if (found != null)
00251                         return found;
00252                 }
00253             }
00254 
00255             return null;
00256         }
