00261         public static void EnsureComponentExists<T, TIfExists>(this Transform t, List<Component> created, List<Component> found, bool recursive) where TIfExists : Component where T : Component
00262         {
00263             if (created != null)
00264                 created.Clear();
00265 
00266             if (found != null)
00267                 found.Clear();
00268 
00269             var existing = t.GetComponent<T>();
00270 
00271             if (!existing && t.GetComponent<TIfExists>())
00272             {
00273                 existing = t.gameObject.AddComponent<T>();
00274                 if (created != null)
00275                     created.Add(existing);
00276             }
00277 
00278             if (existing && found != null)
00279                 found.Add(existing);
00280 
00281             if (recursive)
00282                 for (int i = 0; i < t.childCount; ++i)
00283                     EnsureComponentExists<T, TIfExists>(t.GetChild(i), created, found, true);
00284 
00285         }
