00066         protected void OnValidate()
00067         {
00069             if (PrefabsToInstantiate != null)
00070                 for (int i = 0; i < PrefabsToInstantiate.Count; ++i)
00071                 {
00072                     var prefab = PrefabsToInstantiate[i];
00073                     if (prefab)
00074                         PrefabsToInstantiate[i] = ValidatePrefab(prefab);
00075                 }
00076 
00078             if (SpawnPosition)
00079             {
00080                 if (SpawnPoints == null)
00081                     SpawnPoints = new List<Transform>();
00082 
00083                 SpawnPoints.Add(SpawnPosition);
00084                 SpawnPosition = null;
00085             }
00086         }
00087 
00092         public bool AddPrefabToList(GameObject prefab)
00093         {
00094             var validated = ValidatePrefab(prefab);
00095             if (validated)
00096             {
00097                 // Don't add to list if this prefab already is on the list
00098                 if (PrefabsToInstantiate.Contains(validated))
00099                     return false;
00100 
00101                 // First try to use any null array slots to keep things tidy
00102                 if (PrefabsToInstantiate.Contains(null))
00103                     PrefabsToInstantiate[PrefabsToInstantiate.IndexOf(null)] = validated;
00104                 // Otherwise, just add this prefab.
00105                 else
00106                     PrefabsToInstantiate.Add(validated);
00107                 return true;
00108             }
00109 
00110             return false;
00111 
00112         }
