00119         protected static GameObject ValidatePrefab(GameObject unvalidated)
00120         {
00121             if (unvalidated == null)
00122                 return null;
00123 
00124             //if (!unvalidated.GetComponent<PhotonView>())
00125             //    return null;
00126 
00127 #if UNITY_2018_3_OR_NEWER
00128 
00129             GameObject validated = null;
00130 
00131             if (unvalidated != null)
00132             {
00133 
00134                 if (PrefabUtility.IsPartOfPrefabAsset(unvalidated))
00135                     return unvalidated;
00136 
00137                 var prefabStatus = PrefabUtility.GetPrefabInstanceStatus(unvalidated);
00138                 var isValidPrefab = prefabStatus == PrefabInstanceStatus.Connected || prefabStatus == PrefabInstanceStatus.Disconnected;
00139 
00140                 if (isValidPrefab)
00141                     validated = PrefabUtility.GetCorrespondingObjectFromSource(unvalidated) as GameObject;
00142                 else
00143                     return null;
00144 
00145                 if (!PrefabUtility.GetPrefabAssetPathOfNearestInstanceRoot(validated).Contains("/Resources"))
00146                     Debug.LogWarning("Player Prefab needs to be a Prefab in a Resource folder.");
00147             }
00148 #else
00149             GameObject validated = unvalidated;
00150 
00151             if (unvalidated != null && PrefabUtility.GetPrefabType(unvalidated) != PrefabType.Prefab)
00152                 validated = PrefabUtility.GetPrefabParent(unvalidated) as GameObject;
00153 #endif
00154             return validated;
00155         }
