00171         public virtual void OnJoinedRoom()
00172         {
00173             // Only AutoSpawn if we are a new ActorId. Rejoining should reproduce the objects by server instantiation.
00174             if (AutoSpawnObjects && !PhotonNetwork.LocalPlayer.HasRejoined)
00175             {
00176                 SpawnObjects();
00177             }
00178         }
00179 
00180         public virtual void SpawnObjects()
00181         {
00182             if (this.PrefabsToInstantiate != null)
00183             {
00184                 foreach (GameObject o in this.PrefabsToInstantiate)
00185                 {
00186                     if (o == null)
00187                         continue;
00188 #if UNITY_EDITOR
00189                     Debug.Log("Auto-Instantiating: " + o.name);
00190 #endif
00191                     Vector3 spawnPos; Quaternion spawnRot;
00192                     GetSpawnPoint(out spawnPos, out spawnRot);
00193 
00194 
00195                     var newobj = PhotonNetwork.Instantiate(o.name, spawnPos, spawnRot, 0);
00196                     SpawnedObjects.Push(newobj);
00197                 }
00198             }
00199         }
00200 
00205         public virtual void DespawnObjects(bool localOnly)
00206         {
00207 
00208             while (SpawnedObjects.Count > 0)
00209             {
00210                 var go = SpawnedObjects.Pop();
00211                 if (go)
00212                 {
00213                     if (localOnly)
00214                         Object.Destroy(go);
00215                     else
00216                         PhotonNetwork.Destroy(go);
00217 
00218                 }
00219             }
00220         }
