00235         public virtual void GetSpawnPoint(out Vector3 spawnPos, out Quaternion spawnRot)
00236         {
00237 
00238             // Fetch a point using the Sequence method indicated
00239             Transform point = GetSpawnPoint();
00240 
00241             if (point != null)
00242             {
00243                 spawnPos = point.position;
00244                 spawnRot = point.rotation;
00245             }
00246             else
00247             {
00248                 spawnPos = new Vector3(0, 0, 0);
00249                 spawnRot = new Quaternion(0, 0, 0, 1);
00250             }
00251             
00252             if (UseRandomOffset)
00253             {
00254                 Random.InitState((int)(Time.time * 10000));
00255                 spawnPos += GetRandomOffset();
00256             }
00257         }
00258         
00259 
00266         protected virtual Transform GetSpawnPoint()
00267         {
00268             // Fetch a point using the Sequence method indicated
00269             if (SpawnPoints == null || SpawnPoints.Count == 0)
00270             {
00271                 return null;
00272             }
00273             else
00274             {
00275                 switch (Sequence)
00276                 {
00277                     case SpawnSequence.Connection:
00278                         {
00279                             int id = PhotonNetwork.LocalPlayer.ActorNumber;
00280                             return SpawnPoints[(id == -1) ? 0 : id % SpawnPoints.Count];
00281                         }
00282 
00283                     case SpawnSequence.RoundRobin:
00284                         {
00285                             lastUsedSpawnPointIndex++;
00286                             if (lastUsedSpawnPointIndex >= SpawnPoints.Count)
00287                                 lastUsedSpawnPointIndex = 0;
00288 
00290                             return SpawnPoints == null || SpawnPoints.Count == 0 ? null : SpawnPoints[lastUsedSpawnPointIndex];
00291                         }
00292 
00293                     case SpawnSequence.Random:
00294                         {
00295                             return SpawnPoints[Random.Range(0, SpawnPoints.Count)];
00296                         }
00297 
00298                     default:
00299                         return null;
00300                 }
00301             }
00302         }
00303 
00307         protected virtual Vector3 GetRandomOffset()
00308         {
00309             Vector3 random = Random.insideUnitSphere;
00310             if (ClampY)
00311                 random.y = 0;
00312             return RandomOffset * random.normalized;
00313         }
