00038 
00039                 // if no PackageImportList.txt exists
00040                 if (requiredPackagesListFile.Length == 0)
00041                 {
00042                     Debug.LogError(
00043                         "[Auto Package] : Couldn't find the packages list. Be sure there is a file called PackageImportList in your project");
00044                 }
00045                 else
00046                 {
00047                     packagesToAdd = new List<PackageEntry>();
00048 
00049                     string packageListPath = requiredPackagesListFile[0];
