00051 
00052                     foreach (string line in content)
00053                     {
00054                         string[] split = line.Split('@');
00055 
00056                         // if no version is given, return null
00057                         PackageEntry entry = new PackageEntry
00058                             {name = split[0], version = split.Length > 1 ? split[1] : null};
00059 
00060                         packagesToAdd.Add(entry);
00061                     }
00062 
00063                     // Create a file in library that is queried to see if CheckPackage() has been run already
00064                     ScriptingDefineUtils.SetScriptingDefine(PackageCheckerScriptingDefine);
00065 
00066                     // create a list of compatible packages for current engine version
00067                     compatibleList = Client.SearchAll();
00068 
00069                     while (!compatibleList.IsCompleted)
00070                     {
00071                         if (compatibleList.Status == StatusCode.Failure || compatibleList.Error != null)
00072                         {
00073                             Debug.LogError(compatibleList.Error.message);
00074                             break;
00075                         }
00076                     }
00077 
00078                     // create a list of packages found in the engine
00079                     clientList = Client.List();
00080 
00081                     while (!clientList.IsCompleted)
00082                     {
00083                         if (clientList.Status == StatusCode.Failure || clientList.Error != null)
00084                         {
00085                             Debug.LogError(clientList.Error.message);
00086                             break;
00087                         }
00088                     }
00089 
00090                     addRequests = new AddRequest[packagesToAdd.Count];
00091                     installRequired = new bool[packagesToAdd.Count];
00092 
00093                     // default new packages to install = false. we will mark true after validating they're required
00094                     for (int i = 0; i < installRequired.Length; i++)
00095                     {
00096                         installRequired[i] = false;
00097                     }
00098 
00099                     // build data collections compatible packages for this project, and packages within the project
00100                     List<PackageInfo> compatiblePackages =
00101                         new List<PackageInfo>();
00102                     List<PackageInfo> clientPackages =
00103                         new List<PackageInfo>();
00104 
00105                     foreach (var result in compatibleList.Result)
00106                     {
00107                         compatiblePackages.Add(result);
