00109 
00110                     foreach (var result in clientList.Result)
00111                     {
00112                         clientPackages.Add(result);
00113                     }
00114 
00115                     // check for the latest verified package version for each package that is missing a version
00116                     for (int i = 0; i < packagesToAdd.Count; i++)
00117                     {
00118                         // if a version number is not provided
00119                         if (packagesToAdd[i].version == null)
00120                         {
00121                             foreach (var package in compatiblePackages)
00122                             {
00123                                 // if no latest verified version found, PackageChecker will just install latest release
00124                                 if (packagesToAdd[i].name == package.name && package.versions.verified != string.Empty)
00125                                 {
00126                                     // add latest verified version number to the packagetoadd list version
00127                                     // so that we get the latest verified version only
00128                                     packagesToAdd[i].version = package.versions.verified;
00129 
00130                                     // add to our install list
00131                                     installRequired[i] = true;
00132 
00133                                     //Debug.Log(string.Format("Requested {0}. Latest verified compatible package found: {1}",
00134                                     //    packagesToAdd[i].name, packagesToAdd[i].version));
00135                                 }
00136                             }
00137                         }
00138 
00139                         // we don't need to catch packages that are not installed as their latest version has been collected
00140                         // from the campatiblelist result
00141                         foreach (var package in clientPackages)
00142                         {
00143                             if (packagesToAdd[i].name == package.name)
00144                             {
00145                                 // see what version we have installed
00146                                 switch (CompareVersion(packagesToAdd[i].version, package.version))
00147                                 {
00148                                     // latest verified is ahead of installed version
00149                                     case 1:
00150                                         installRequired[i] = EditorUtility.DisplayDialog("Confirm Package Upgrade",
00151                                             $"The version of \"{packagesToAdd[i].name}\" in this project is {package.version}. The latest verified " +
