00154 
00155                                         Debug.Log(
00156                                             $"<b>Package version behind</b>: {package.packageId} is behind latest verified " +
00157                                             $"version {package.versions.verified}. prompting user install");
00158                                         break;
00159 
00160                                     // latest verified matches installed version
00161                                     case 0:
00162                                         installRequired[i] = false;
00163 
00164                                         Debug.Log(
00165                                             $"<b>Package version match</b>: {package.packageId} matches latest verified version " +
00166                                             $"{package.versions.verified}. Skipped install");
00167                                         break;
00168 
00169                                     // latest verified is behind installed version
00170                                     case -1:
00171                                         installRequired[i] = EditorUtility.DisplayDialog("Confirm Package Downgrade",
00172                                             $"The version of \"{packagesToAdd[i].name}\" in this project is {package.version}. The latest verified version is {packagesToAdd[i].version}. " +
00173                                             $"{package.version} is unverified. Would you like to downgrade it to the latest verified version? " +
00174                                             "(Recommended)", "Yes", "No");
00175 
00176                                         Debug.Log(
00177                                             $"<b>Package version ahead</b>: {package.packageId} is newer than latest verified " +
00178                                             $"version {package.versions.verified}, skipped install");
00179                                         break;
00180                                 }
00181                             }
00182                         }
00183                     }
00184 
00185                     // install our packages and versions
00186                     for (int i = 0; i < packagesToAdd.Count; i++)
00187                     {
00188                         if (installRequired[i])
00189                         {
00190                             addRequests[i] = InstallSelectedPackage(packagesToAdd[i].name, packagesToAdd[i].version);
00191                         }
