00199         private static AddRequest InstallSelectedPackage(string packageName, string packageVersion)
00200         {
00201             if (packageVersion != null)
00202             {
00203                 packageName = packageName + "@" + packageVersion;
00204                 Debug.Log($"<b>Adding package</b>: {packageName}");
00205             }
00206 
00207             AddRequest newPackage = Client.Add(packageName);
00208 
00209             while (!newPackage.IsCompleted)
00210             {
00211                 if (newPackage.Status == StatusCode.Failure || newPackage.Error != null)
00212                 {
00213                     Debug.LogError(newPackage.Error.message);
00214                     return null;
00215                 }
00216             }
00217 
00218             return newPackage;
00219         }
