00227         public static int CompareVersion(string latestVerifiedVersion, string projectVersion)
00228         {
00229             string[] latestVersionSplit = latestVerifiedVersion.Split('.');
00230             string[] projectVersionSplit = projectVersion.Split('.');
00231             int iteratorA = 0;
00232             int iteratorB = 0;
00233 
00234             while (iteratorA < latestVersionSplit.Length || iteratorB < projectVersionSplit.Length)
00235             {
00236                 int latestVerified = 0;
00237                 int installed = 0;
00238 
00239                 if (iteratorA < latestVersionSplit.Length)
00240                 {
00241                     latestVerified = Convert.ToInt32(latestVersionSplit[iteratorA]);
00242                 }
00243 
00244                 if (iteratorB < projectVersionSplit.Length)
00245                 {
00246                     installed = Convert.ToInt32(projectVersionSplit[iteratorB]);
00247                 }
00248 
00249                 // latest verified is ahead of installed version
00250                 if (latestVerified > installed) return 1;
00251 
00252                 // latest verified is behind installed version
00253                 if (latestVerified < installed) return -1;
00254 
00255                 iteratorA++;
00256                 iteratorB++;
00257             }
00258 
00259             // if the version is the same
00260             return 0;
00261         }
