00156         public void OnTriggerEnter(Collider other)
00157         {
00158             if (!photonView.IsMine)
00159             {
00160                 return;
00161             }
00162 
00163 
00164             // We are only interested in Beamers
00165             // we should be using tags but for the sake of distribution, let's simply check by name.
