00179         public void OnTriggerStay(Collider other)
00180         {
00181             // we dont' do anything if we are not the local player.
00182             if (!photonView.IsMine)
00183             {
00184                 return;
00185             }
00186 
00187             // We are only interested in Beamers
00188             // we should be using tags but for the sake of distribution, let's simply check by name.
00189             if (!other.name.Contains("Beam"))
00190             {
00191                 return;
00192             }
00193 
00194             // we slowly affect health when beam is constantly hitting us, so player has to move to prevent death.
00195             this.Health -= 0.1f*Time.deltaTime;
