00241         void ProcessInputs()
00242         {
00243             if (Input.GetButtonDown("Fire1"))
00244             {
00245                 // we don't want to fire when we interact with UI buttons for example. IsPointerOverGameObject really means IsPointerOver*UI*GameObject
00246                 // notice we don't use on on GetbuttonUp() few lines down, because one can mouse down, move over a UI element and release, which would lead to not lower the isFiring Flag.
00247                 if (EventSystem.current.IsPointerOverGameObject())
00248                 {
00249                     //  return;
00250                 }
00251 
00252                 if (!this.IsFiring)
00253                 {
00254                     this.IsFiring = true;
00255                 }
00256             }
00257 
00258             if (Input.GetButtonUp("Fire1"))
00259             {
00260                 if (this.IsFiring)
00261                 {
00262                     this.IsFiring = false;
00263                 }
00264             }
00265         }
