00271         public void OnPhotonSerializeView(PhotonStream stream, PhotonMessageInfo info)
00272         {
00273             if (stream.IsWriting)
00274             {
00275                 // We own this player: send the others our data
00276                 stream.SendNext(this.IsFiring);
00277                 stream.SendNext(this.Health);
00278             }
00279             else
00280             {
00281                 // Network player, receive data
00282                 this.IsFiring = (bool)stream.ReceiveNext();
00283                 this.Health = (float)stream.ReceiveNext();
00284             }
00285         }
