00181         // used to register for various events (post-load)
00182         private static void OnDelayCall()
00183         {
00184             //Debug.Log("OnDelayCall()");
00185 
00186             postInspectorUpdate = true;
00187 
00188             EditorApplication.playModeStateChanged -= PlayModeStateChanged;
00189             EditorApplication.playModeStateChanged += PlayModeStateChanged;
00190 
00191             #if UNITY_2021_1_OR_NEWER
00192             CompilationPipeline.compilationStarted -= OnCompileStarted21;
00193             CompilationPipeline.compilationStarted += OnCompileStarted21;
00194             #else
00195             CompilationPipeline.assemblyCompilationStarted -= OnCompileStarted;
00196             CompilationPipeline.assemblyCompilationStarted += OnCompileStarted;
00197             #endif
00198 
00199             #if (UNITY_2018 || UNITY_2018_1_OR_NEWER)
00200             EditorApplication.projectChanged -= OnProjectChanged;
00201             EditorApplication.projectChanged += OnProjectChanged;
00202             #else
00203             EditorApplication.projectWindowChanged -= OnProjectChanged;
00204             EditorApplication.projectWindowChanged += OnProjectChanged;
00205             #endif
00206 
00207 
00208             if (!EditorApplication.isPlaying && !EditorApplication.isPlayingOrWillChangePlaymode)
00209             {
00210                 OnProjectChanged(); // call this initially from here, as the project change events happened earlier (on start of the Editor)
00211                 PhotonEditor.UpdateRpcList();
00212             }
00213         }
