00217         // called in editor, opens wizard for initial setup, keeps scene PhotonViews up to date and closes connections when compiling (to avoid issues)
00218         private static void OnProjectChanged()
00219         {
00220             PhotonEditorUtils.ProjectChangedWasCalled = true;
00221 
00222 
00223             // Prevent issues with Unity Cloud Builds where ServerSettings are not found.
00224             // Also, within the context of a Unity Cloud Build, ServerSettings is already present anyway.
00225             #if UNITY_CLOUD_BUILD
00226             return;
00227             #endif
00228 
00229             if (PhotonNetwork.PhotonServerSettings == null || PhotonNetwork.PhotonServerSettings.AppSettings == null || string.IsNullOrEmpty(PhotonNetwork.PhotonServerSettings.AppSettings.AppIdRealtime))
00230             {
00231                 PhotonNetwork.LoadOrCreateSettings(true);
00232             }
00233 
00234             if (PhotonNetwork.PhotonServerSettings == null)
00235             {
00236                 // the PhotonServerSettings are loaded or created. If both fails, the Editor should probably not run (anymore).
00237                 return;
00238             }
00239 
00240             PunSceneSettings.SanitizeSceneSettings();
00241 
00242 
00243             // serverSetting is null when the file gets deleted. otherwise, the wizard should only run once and only if hosting option is not (yet) set
00244             if (!PhotonNetwork.PhotonServerSettings.DisableAutoOpenWizard)
00245             {
00246                 ShowRegistrationWizard();
00247                 PhotonNetwork.PhotonServerSettings.DisableAutoOpenWizard = true;
00248                 PhotonEditor.SaveSettings();
00249             }
00250         }
