00260         private static void OnCompileStarted(string obj)
00261         {
00262             if (PhotonNetwork.IsConnected)
00263             {
00264                 // log warning, unless there was one recently
00265                 if (EditorApplication.timeSinceStartup - lastWarning > 3)
00266                 {
00267                     Debug.LogWarning(CurrentLang.WarningPhotonDisconnect);
00268                     lastWarning = EditorApplication.timeSinceStartup;
00269                 }
00270 
00271                 PhotonNetwork.Disconnect();
00272                 PhotonNetwork.NetworkingClient.LoadBalancingPeer.DispatchIncomingCommands();
00273                 #if UNITY_2019_4_OR_NEWER && UNITY_EDITOR
00274                 EditorApplication.ExitPlaymode();
00275                 #endif
00276             }
00277         }
