00283             //Debug.Log("OnDidReloadScripts() postInspectorUpdate: "+postInspectorUpdate + " isPlayingOrWillChangePlaymode: "+EditorApplication.isPlayingOrWillChangePlaymode);
00284             if (postInspectorUpdate && !EditorApplication.isPlayingOrWillChangePlaymode)
00285             {
