00290         private static void PlayModeStateChanged(PlayModeStateChange state)
00291         {
00292             //Debug.Log("PlayModeStateChanged");
00293             if (EditorApplication.isPlaying || !EditorApplication.isPlayingOrWillChangePlaymode)
00294             {
00295                 return;
00296             }
00297 
00298             if (string.IsNullOrEmpty(PhotonNetwork.PhotonServerSettings.AppSettings.AppIdRealtime) && !PhotonNetwork.PhotonServerSettings.AppSettings.IsMasterServerAddress)
00299             {
00300                 EditorUtility.DisplayDialog(CurrentLang.SetupWizardWarningTitle, CurrentLang.SetupWizardWarningMessage, CurrentLang.OkButton);
00301             }
00302         }
