00386         protected virtual void UiSetupApp()
00387         {
00388             GUI.skin.label.wordWrap = true;
00389             if (!this.isSetupWizard)
00390             {
00391                 GUILayout.BeginHorizontal();
00392                 GUILayout.FlexibleSpace();
00393                 if (GUILayout.Button(CurrentLang.MainMenuButton, GUILayout.ExpandWidth(false)))
00394                 {
00395                     this.photonSetupState = PhotonSetupStates.MainUi;
00396                 }
00397 
00398                 GUILayout.EndHorizontal();
00399             }
00400 
00401 
00402             // setup header
00403             this.UiTitleBox(CurrentLang.SetupWizardTitle, BackgroundImage);
00404 
00405             // setup info text
00406             GUI.skin.label.richText = true;
00407             GUILayout.Label(CurrentLang.SetupWizardInfo);
00408 
00409             // input of appid or mail
00410             EditorGUILayout.Separator();
00411             GUILayout.Label(CurrentLang.EmailOrAppIdLabel);
00412             this.minimumInput = false;
00413             this.useMail = false;
00414             this.useAppId = false;
00415             this.mailOrAppId = EditorGUILayout.TextField(this.mailOrAppId);
00416             if (!string.IsNullOrEmpty(this.mailOrAppId))
00417             {
00418                 this.mailOrAppId = this.mailOrAppId.Trim(); // note: we trim all input
00419                 if (AccountService.IsValidEmail(this.mailOrAppId))
00420                 {
00421                     // input should be a mail address
00422                     this.useMail = true;
00423 
00424                     // check if the current input equals earlier input, which is known to be registered already
00425                     this.minimumInput = !this.mailOrAppId.Equals(this.emailSentToAccount) || !this.emailSentToAccountIsRegistered;
00426                 }
00427                 else if (ServerSettings.IsAppId(this.mailOrAppId))
00428                 {
00429                     // this should be an appId
00430                     this.minimumInput = true;
00431                     this.useAppId = true;
00432                 }
00433             }
00434 
00435             // button to skip setup
00436             GUILayout.BeginHorizontal();
00437             GUILayout.FlexibleSpace();
00438             if (GUILayout.Button(CurrentLang.SkipButton, GUILayout.Width(100)))
00439             {
00440                 this.photonSetupState = PhotonSetupStates.GoEditPhotonServerSettings;
00441                 this.useSkip = true;
00442                 this.useMail = false;
00443                 this.useAppId = false;
00444             }
00445 
00446             // SETUP button
00447             EditorGUI.BeginDisabledGroup(!this.minimumInput);
00448             if (GUILayout.Button(CurrentLang.SetupButton, GUILayout.Width(100)))
00449             {
00450                 this.useSkip = false;
00451                 GUIUtility.keyboardControl = 0;
00452                 if (this.useMail)
00453                 {
00454                     this.RegisterWithEmail(this.mailOrAppId);       // sets state
00455                 }
00456                 else if (this.useAppId)
00457                 {
00458                     this.photonSetupState = PhotonSetupStates.GoEditPhotonServerSettings;
00459                     Undo.RecordObject(PhotonNetwork.PhotonServerSettings, "Update PhotonServerSettings for PUN");
00460                     PhotonNetwork.PhotonServerSettings.UseCloud(this.mailOrAppId);
00461                     PhotonEditor.SaveSettings();
00462                 }
00463             }
00464             EditorGUI.EndDisabledGroup();
00465             GUILayout.FlexibleSpace();
00466             GUILayout.EndHorizontal();
00467 
00468 
00469             // existing account needs to fetch AppId online
00470             if (this.photonSetupState == PhotonSetupStates.EmailAlreadyRegistered)
00471             {
00472                 // button to open dashboard and get the AppId
00473                 GUILayout.Space(15);
00474                 GUILayout.Label(CurrentLang.AlreadyRegisteredInfo);
00475 
00476 
00477                 GUILayout.BeginHorizontal();
00478                 GUILayout.FlexibleSpace();
