00480                 {
00481                     Application.OpenURL(string.Concat(UrlCloudDashboard, Uri.EscapeUriString(this.mailOrAppId)));
00482                     this.mailOrAppId = string.Empty;
00483                 }
00484                 GUILayout.FlexibleSpace();
00485                 GUILayout.EndHorizontal();
00486             }
00487 
00488 
00489             else if (this.photonSetupState == PhotonSetupStates.GoEditPhotonServerSettings)
00490             {
00491                 if (!this.highlightedSettings)
00492                 {
00493                     this.highlightedSettings = true;
00494                     HighlightSettings();
00495                 }
