00500                     GUILayout.Label(CurrentLang.SkipRegistrationInfo);
00501                 }
00502                 else if (this.useMail)
00503                 {
00504                     GUILayout.Label(CurrentLang.RegisteredNewAccountInfo);
00505                 }
00506                 else if (this.useAppId)
00507                 {
00508                     GUILayout.Label(CurrentLang.AppliedToSettingsInfo);
00509                 }
00510 
00511 
00512                 // setup-complete info
00513                 GUILayout.Space(15);
00514                 GUILayout.Label(CurrentLang.SetupCompleteInfo);
00515 
