00617         private AccountService serviceClient;
00618         protected virtual void RegisterWithEmail(string email)
00619         {
00620             List<ServiceTypes> types = new List<ServiceTypes>();
00621             types.Add(ServiceTypes.Pun);
00622             if (PhotonEditorUtils.HasChat)
00623             {
00624                 types.Add(ServiceTypes.Chat);
00625             }
00626             if (PhotonEditorUtils.HasVoice)
00627             {
00628                 types.Add(ServiceTypes.Voice);
00629             }
00630 
00631 
00632             if (this.serviceClient == null)
00633             {
00634                 this.serviceClient = new AccountService();
00635                 this.serviceClient.CustomToken = CustomToken;
00636                 this.serviceClient.CustomContext = CustomContext;
00637             }
00638             else
00639             {
00640                 // while RegisterByEmail will check RequestPendingResult below, it would also display an error message. no needed in this case
00641                 if (this.serviceClient.RequestPendingResult)
00642                 {
00643                     Debug.LogWarning("Registration request is pending a response. Please wait.");
00644                     return;
00645                 }
00646             }
00647 
00648             this.emailSentToAccount = email;
00649             this.emailSentToAccountIsRegistered = false;
00650 
00651             if (this.serviceClient.RegisterByEmail(email, types, RegisterWithEmailSuccessCallback, RegisterWithEmailErrorCallback, "PUN"+PhotonNetwork.PunVersion))
00652             {
00653                 this.photonSetupState = PhotonSetupStates.EmailRegistrationPending;
00654                 EditorUtility.DisplayProgressBar(CurrentLang.ConnectionTitle, CurrentLang.ConnectionInfo, 0.5f);
00655             }
00656             else
00657             {
00658                 this.DisplayErrorMessage("Email registration request could not be sent. Retry again or check error logs and contact support.");
00659             }
00660         }
00661 
00662         private void RegisterWithEmailSuccessCallback(AccountServiceResponse res)
00663         {
00664             EditorUtility.ClearProgressBar();
00665             this.emailSentToAccountIsRegistered = true; // email is either registered now, or was already
00666 
00667             if (res.ReturnCode == AccountServiceReturnCodes.Success)
00668             {
00669                 string key = ((int) ServiceTypes.Pun).ToString();
00670                 string appId;
00671                 if (res.ApplicationIds.TryGetValue(key, out appId))
00672                 {
00673                     this.mailOrAppId = appId;
00674                     PhotonNetwork.PhotonServerSettings.UseCloud(this.mailOrAppId, null);
00675                     key = ((int) ServiceTypes.Chat).ToString();
00676                     if (res.ApplicationIds.TryGetValue(key, out appId))
00677                     {
00678                         PhotonNetwork.PhotonServerSettings.AppSettings.AppIdChat = appId;
00679                     }
00680                     else if (PhotonEditorUtils.HasChat)
00681                     {
00682                         Debug.LogWarning("Registration successful but no Chat AppId returned");
00683                     }
00684                     key = ((int) ServiceTypes.Voice).ToString();
00685                     if (res.ApplicationIds.TryGetValue(key, out appId))
00686                     {
00687                         PhotonNetwork.PhotonServerSettings.AppSettings.AppIdVoice = appId;
00688                     }
00689                     else if (PhotonEditorUtils.HasVoice)
00690                     {
00691                         Debug.LogWarning("Registration successful but no Voice AppId returned");
00692                     }
00693                     PhotonEditor.SaveSettings();
00694                     this.photonSetupState = PhotonSetupStates.GoEditPhotonServerSettings;
00695                 }
00696                 else
00697                 {
00698                     DisplayErrorMessage("Registration successful but no PUN AppId returned");
00699                 }
00700             }
00701             else
00702             {
00703                 PhotonEditor.SaveSettings();
00704 
00705                 if (res.ReturnCode == AccountServiceReturnCodes.EmailAlreadyRegistered)
00706                 {
00707                     this.photonSetupState = PhotonSetupStates.EmailAlreadyRegistered;
00708                 }
00709                 else
00710                 {
00711                     DisplayErrorMessage(res.Message);
00712                 }
00713             }
00714         }
00715 
00716         private void RegisterWithEmailErrorCallback(string error)
00717         {
00718             EditorUtility.ClearProgressBar();
00719             DisplayErrorMessage(error);
00720         }
00721 
00722         private void DisplayErrorMessage(string error)
00723         {
00724             EditorUtility.DisplayDialog(CurrentLang.ErrorTextTitle, error, CurrentLang.OkButton);
00725             this.photonSetupState = PhotonSetupStates.RegisterForPhotonCloud;
00726         }
