00743         #region RPC List Handling
00744 
00745 
00746         public static void UpdateRpcList()
00747         {
00748             //Debug.Log("UpdateRpcList()");
00749 
00750             if (PhotonNetwork.PhotonServerSettings == null)
00751             {
00752                 Debug.LogWarning("UpdateRpcList() wasn not able to access the PhotonServerSettings. Not updating the RPCs.");
00753                 return;
00754             }
00755 
00756 
00757             // check all "script assemblies" for methods with PunRPC attribute
00758             List<string> additionalRpcs = new List<string>();       // not yet listed rpc-method names go here
00759             List<string> allRpcs = new List<string>();
00760 
00761 
00762             #if UNITY_2019_2_OR_NEWER
00763 
00764             // we can make use of the new TypeCache to find methods with PunRPC attribute
00765             var extractedMethods = TypeCache.GetMethodsWithAttribute<PunRPC>();
00766             foreach (var methodInfo in extractedMethods)
00767             {
00768                 allRpcs.Add(methodInfo.Name);
00769                 if (!PhotonNetwork.PhotonServerSettings.RpcList.Contains(methodInfo.Name) && !additionalRpcs.Contains(methodInfo.Name))
00770                 {
00771                     additionalRpcs.Add(methodInfo.Name);
00772                 }
00773             }
00774 
00775             #else
00776 
00777             System.Reflection.Assembly[] assemblies = System.AppDomain.CurrentDomain.GetAssemblies().Where(a => !(a.ManifestModule is System.Reflection.Emit.ModuleBuilder)).ToArray();
00778 
00779             foreach (var assembly in assemblies)
00780             {
00781                 if (!assembly.Location.Contains("ScriptAssemblies") || assembly.FullName.StartsWith("Assembly-CSharp-Editor"))
00782                 {
00783                     continue;
00784                 }
00785 
00786                 var types = assembly.GetTypes().Where(t => t.IsSubclassOf(typeof(MonoBehaviour)));
00787                 var methodInfos = types.SelectMany(t => t.GetMethods(BindingFlags.Public | BindingFlags.NonPublic | BindingFlags.Instance));
00788                 var methodNames = methodInfos.Where(m => m.IsDefined(typeof(PunRPC), false)).Select(mi => mi.Name).ToArray();
00789                 var additional = methodNames.Where(n => !PhotonNetwork.PhotonServerSettings.RpcList.Contains(n) && !additionalRpcs.Contains(n));
00790 
00791                 allRpcs.AddRange(methodNames);
00792                 additionalRpcs.AddRange(additional);
00793             }
00794 
00795             #endif
00796 
00797 
00798             if (additionalRpcs.Count <= 0)
00799             {
00800                 //Debug.Log("UpdateRPCs did not found new.");
00801                 return;
00802             }
00803 
00804 
00805             if (additionalRpcs.Count + PhotonNetwork.PhotonServerSettings.RpcList.Count >= byte.MaxValue)
00806             {
00807                 if (allRpcs.Count <= byte.MaxValue)
00808                 {
00809                     bool clearList = EditorUtility.DisplayDialog(CurrentLang.IncorrectRPCListTitle, CurrentLang.IncorrectRPCListLabel, CurrentLang.RemoveOutdatedRPCsLabel, CurrentLang.CancelButton);
00810                     if (clearList)
00811                     {
00812                         PhotonNetwork.PhotonServerSettings.RpcList.Clear();
00813                         additionalRpcs = allRpcs.Distinct().ToList();   // we add all unique names
00814                     }
00815                     else
00816                     {
00817                         return;
00818                     }
00819                 }
00820                 else
00821                 {
00822                     EditorUtility.DisplayDialog(CurrentLang.FullRPCListTitle, CurrentLang.FullRPCListLabel, CurrentLang.SkipRPCListUpdateLabel);
00823                     return;
00824                 }
00825             }
00826 
00827 
00828             additionalRpcs.Sort();
00829             Undo.RecordObject(PhotonNetwork.PhotonServerSettings, "RPC-list update of PUN.");
00830             PhotonNetwork.PhotonServerSettings.RpcList.AddRange(additionalRpcs);
00831             EditorUtility.SetDirty(PhotonNetwork.PhotonServerSettings);
00832 
00833             //Debug.Log("Updated RPCs. Added: "+additionalRpcs.Count);
00834         }
00835 
00836 
00837         public static void ClearRpcList()
00838         {
00839             bool clearList = EditorUtility.DisplayDialog(CurrentLang.PUNNameReplaceTitle, CurrentLang.PUNNameReplaceLabel, CurrentLang.RPCListCleared, CurrentLang.CancelButton);
00840             if (clearList)
00841             {
00842                 ServerSettings serverSettings = PhotonNetwork.PhotonServerSettings;
00843 
00844                 Undo.RecordObject(serverSettings, "RPC-list cleared for PUN.");
00845                 serverSettings.RpcList.Clear();
00846                 EditorUtility.SetDirty(serverSettings);
00847 
00848                 Debug.LogWarning(CurrentLang.ServerSettingsCleanedWarning);
00849             }
00850         }
