00297         static Rect DoContainerBody(float height)
00298         {
00299             Rect controlRect = EditorGUILayout.GetControlRect(false, height);
00300             controlRect.yMin -= 3;
00301             controlRect.yMax -= 2;
00302 
00303             int controlID = GUIUtility.GetControlID(FocusType.Passive, controlRect);
00304 
00305             if (Event.current.type == EventType.Repaint)
00306             {
00307                 PhotonGUI.DefaultContainerStyle.Draw(controlRect, GUIContent.none, controlID);
00308             }
00309 
00310             return controlRect;
00311         }
