00322         static bool DoContainerHeaderFoldout(string headline, bool foldout, System.Action buttonAction = null, string buttonLabel = null, float buttonWidth = 48)
00323         {
00324             bool showButton = buttonAction != null;
00325 
00326             Rect rect = DoContainerHeader("", 27, 0f);
00327 
00328             // Shorten foldout label if button is present, so it doesn't interfere with clicking.
00329             float foldoutWidth = rect.width - (showButton ? 15 + buttonWidth: 15);
00330             Rect foldoutRect = new Rect(rect.xMin + 15, rect.yMin + 5, foldoutWidth, 16);
00331 
00332             bool expanded = EditorGUI.Foldout(foldoutRect, foldout, headline, FoldoutBold);
00333 
00334             // If a button is defined show it, and invoke action on click.
00335             if (showButton && GUI.Button(new Rect(foldoutRect) { x = foldoutRect.xMax, height = 17, width = buttonWidth - 4 }, buttonLabel == null ? "" : buttonLabel))
00336             {
00337                 buttonAction.Invoke();
00338             }
00339 
00340             return expanded;
00341         }
00342 
00343         static Rect DoContainerHeader(string headline, float height, float contentOffset)
00344         {
00345             GUILayout.Space(5);
00346             Rect controlRect = EditorGUILayout.GetControlRect(false, height);
00347 
00348             int controlID = GUIUtility.GetControlID(FocusType.Passive, controlRect);
00349 
00350             if (Event.current.type == EventType.Repaint)
00351             {
00352                 PhotonGUI.DefaultTitleStyle.Draw(controlRect, GUIContent.none, controlID);
00353 
00354                 Rect labelRect = new Rect(controlRect.xMin + 5 + contentOffset, controlRect.yMin + 5, controlRect.width, controlRect.height);
00355                 GUI.Label(labelRect, headline, EditorStyles.boldLabel);
00356             }
00357 
00358             return controlRect;
00359         }
