00146         void IInRoomCallbacks.OnPlayerPropertiesUpdate(Player targetPlayer, Hashtable changedProps)
00147         {
00148             object temp;
00149             if (changedProps.TryGetValue(TeamPlayerProp, out temp))
00150             {
00151                 if (temp == null)
00152                 {
00153                     foreach (byte code in playersPerTeam.Keys)
00154                     {
00155                         if (playersPerTeam[code].Remove(targetPlayer))
00156                         {
00157                             if (PlayerLeftTeam != null)
00158                             {
00159                                 PlayerLeftTeam(targetPlayer, teamsByCode[code]);
00160                             }
00161                             break;
00162                         }
00163                     }
00164                 } 
00165                 else if (temp is byte)
00166                 {
00167                     byte teamCode = (byte) temp;
00168                     // check if player switched teams, remove from previous team 
00169                     foreach (byte code in playersPerTeam.Keys)
00170                     {
00171                         if (code == teamCode)
00172                         {
00173                             continue;
00174                         }
00175                         if (playersPerTeam[code].Remove(targetPlayer))
00176                         {
00177                             if (PlayerLeftTeam != null)
00178                             {
00179                                 PlayerLeftTeam(targetPlayer, teamsByCode[code]);
00180                             }
00181                             break;
00182                         }
00183                     }
00184                     PhotonTeam team = teamsByCode[teamCode];
00185                     if (!playersPerTeam[teamCode].Add(targetPlayer))
00186                     {
00187                         Debug.LogWarningFormat("Unexpected situation while setting team {0} for player {1}, updating teams for all", team, targetPlayer);
00188                         this.UpdateTeams();
00189                     }
00190                     if (PlayerJoinedTeam != null)
00191                     {
00192                         PlayerJoinedTeam(targetPlayer, team);
00193                     }
00194                 }
00195                 else
00196                 {
00197                     Debug.LogErrorFormat("Unexpected: custom property key {0} should have of type byte, instead we got {1} of type {2}. Player: {3}", 
00198                         TeamPlayerProp, temp, temp.GetType(), targetPlayer);
00199                 }
00200             }
00201         }
