00203         void IInRoomCallbacks.OnPlayerLeftRoom(Player otherPlayer)
00204         {
00205             if (otherPlayer.IsInactive)
00206             {
00207                 return;
00208             }
00209             PhotonTeam team = otherPlayer.GetPhotonTeam();
00210             if (team != null && !playersPerTeam[team.Code].Remove(otherPlayer))
00211             {
00212                 Debug.LogWarningFormat("Unexpected situation while removing player {0} who left from team {1}, updating teams for all", otherPlayer, team);
00213                 // revert to 'brute force' in case of unexpected situation
00214                 this.UpdateTeams();
00215             }
00216         }
00217 
00218         void IInRoomCallbacks.OnPlayerEnteredRoom(Player newPlayer)
00219         {
00220             PhotonTeam team = newPlayer.GetPhotonTeam();
00221             if (team == null)
00222             {
00223                 return;
00224             }
00225             if (playersPerTeam[team.Code].Contains(newPlayer))
00226             {
00227                 // player rejoined w/ same team
00228                 return;
00229             }
00230             // check if player rejoined w/ different team, remove from previous team 
00231             foreach (var key in teamsByCode.Keys)
00232             {
00233                 if (playersPerTeam[key].Remove(newPlayer))
00234                 {
00235                     break;
00236                 }
00237             }
00238             if (!playersPerTeam[team.Code].Add(newPlayer))
00239             {
00240                 Debug.LogWarningFormat("Unexpected situation while adding player {0} who joined to team {1}, updating teams for all", newPlayer, team);
00241                 // revert to 'brute force' in case of unexpected situation
00242                 this.UpdateTeams();
00243             }
00244         }
