00250         private void UpdateTeams()
00251         {
00252             this.ClearTeams();
00253             for (int i = 0; i < PhotonNetwork.PlayerList.Length; i++)
00254             {
00255                 Player player = PhotonNetwork.PlayerList[i];
00256                 PhotonTeam playerTeam = player.GetPhotonTeam();
00257                 if (playerTeam != null)
00258                 {
00259                     playersPerTeam[playerTeam.Code].Add(player);
00260                 }
00261             }
00262         }
00263 
00264         private void ClearTeams()
00265         {
00266             foreach (var key in playersPerTeam.Keys)
00267             {
00268                 playersPerTeam[key].Clear();
00269             }
00270         }
