00374         public bool TryGetTeamMatesOfPlayer(Player player, out Player[] teamMates)
00375         {
00376             teamMates = null;
00377             if (player == null)
00378             {
00379                 return false;
00380             }
00381             PhotonTeam team = player.GetPhotonTeam();
00382             if (team == null)
00383             {
00384                 return false;
00385             }
00386             HashSet<Player> players;
00387             if (this.playersPerTeam.TryGetValue(team.Code, out players))
00388             {
00389                 if (!players.Contains(player))
00390                 {
00391                     Debug.LogWarningFormat("Unexpected situation while getting team mates of player {0} who is joined to team {1}, updating teams for all", player, team);
00392                     // revert to 'brute force' in case of unexpected situation
00393                     this.UpdateTeams();
00394                 }
00395                 teamMates = new Player[players.Count - 1];
00396                 int i = 0;
00397                 foreach (var p in players)
00398                 {
00399                     if (p.Equals(player))
00400                     {
00401                         continue;
00402                     }
00403                     teamMates[i] = p;
00404                     i++;
00405                 }
00406                 return true;
00407             }
00408             return false;
00409         }
