00513         public static bool JoinTeam(this Player player, PhotonTeam team)
00514         {
00515             if (team == null)
00516             {
00517                 Debug.LogWarning("JoinTeam failed: PhotonTeam provided is null");
00518                 return false;
00519             }
00520             PhotonTeam currentTeam = player.GetPhotonTeam();
00521             if (currentTeam != null)
00522             {
00523                 Debug.LogWarningFormat("JoinTeam failed: player ({0}) is already joined to a team ({1}), call SwitchTeam instead", player, team);
00524                 return false;
00525             }
00526             return player.SetCustomProperties(new Hashtable { { PhotonTeamsManager.TeamPlayerProp, team.Code } });
00527         }
