00557         public static bool SwitchTeam(this Player player, PhotonTeam team)
00558         {
00559             if (team == null)
00560             {
00561                 Debug.LogWarning("SwitchTeam failed: PhotonTeam provided is null");
00562                 return false;
00563             }
00564             PhotonTeam currentTeam = player.GetPhotonTeam();
00565             if (currentTeam == null)
00566             {
00567                 Debug.LogWarningFormat("SwitchTeam failed: player ({0}) was not joined to any team, call JoinTeam instead", player);
00568                 return false;
00569             }
00570             if (currentTeam.Code == team.Code)
00571             {
00572                 Debug.LogWarningFormat("SwitchTeam failed: player ({0}) is already joined to the same team {1}", player, team);
00573                 return false;
00574             }
00575             return player.SetCustomProperties(new Hashtable { { PhotonTeamsManager.TeamPlayerProp, team.Code } },
00576                 new Hashtable { { PhotonTeamsManager.TeamPlayerProp, currentTeam.Code }});
00577         }
