00055         private static void GetImageSize( byte[] imageData, out int width, out int height )
00056         {
00057             width = ReadInt( imageData, 3 + 15 );
00058             height = ReadInt( imageData, 3 + 15 + 2 + 2 );
00059         }
00060 
00061         private static int ReadInt( byte[] imageData, int offset )
00062         {
00063             return ( imageData[ offset ] << 8 ) | imageData[ offset + 1 ];
00064         }
00065 
00066         private Texture LoadTexture(string textureName, string proSkin, string lightSkin)
00067         {
00068             string skin = EditorGUIUtility.isProSkin ? proSkin : lightSkin;
00069             // Get image data (PNG) from base64 encoded strings.
00070             byte[] imageData = Convert.FromBase64String( skin );
00071             // Gather image size from image data.
00072             int texWidth, texHeight;
00073             GetImageSize( imageData, out texWidth, out texHeight );
00074             // Generate texture asset.
00075             var tex = new Texture2D( texWidth, texHeight, TextureFormat.ARGB32, false, true );
00076             tex.hideFlags = HideFlags.HideAndDontSave;
00077             tex.name = textureName;
00078             tex.filterMode = FilterMode.Point;
00079             tex.LoadImage( imageData );
00080             return tex;
00081         }
