00083         public override void OnInspectorGUI()
00084         {
00085             if (!Application.isPlaying)
00086             {
00087                 DrawTeamsList();
00088                 return;
00089             }
00090             PhotonTeam[] availableTeams = photonTeams.GetAvailableTeams();
00091             if (availableTeams != null)
00092             {
00093                 EditorGUI.indentLevel++;
00094                 foreach (var availableTeam in availableTeams)
00095                 {
00096                     if (!foldouts.ContainsKey(availableTeam.Code))
00097                     {
00098                         foldouts[availableTeam.Code] = true;
00099                     }
00100                     Player[] teamMembers;
00101                     if (photonTeams.TryGetTeamMembers(availableTeam, out teamMembers) && teamMembers != null)
00102                     {
00103                         foldouts[availableTeam.Code] = EditorGUILayout.Foldout(foldouts[availableTeam.Code],
00104                             string.Format("{0} ({1})", availableTeam.Name, teamMembers.Length));
00105                     }
00106                     else
00107                     {
00108                         foldouts[availableTeam.Code] = EditorGUILayout.Foldout(foldouts[availableTeam.Code],
00109                             string.Format("{0} (0)", availableTeam.Name));
00110                     }
00111                     if (foldouts[availableTeam.Code] && teamMembers != null)
00112                     {
00113                         EditorGUI.indentLevel++;
00114                         foreach (var player in teamMembers)
00115                         {
00116                             EditorGUILayout.LabelField(string.Empty, string.Format("{0} {1}", player, player.IsLocal ? " - You -" : string.Empty));
00117                         }
00118                         EditorGUI.indentLevel--;
00119                     }
00120                 }
00121                 EditorGUI.indentLevel--;
00122             }
00123         }
