00136                 // force latest data to avoid initial drifts when player is instantiated.
00137                 if (m_firstTake)
00138                 {
00139                     m_firstTake = false;
00140 
00141                     if (this.m_PositionModel.SynchronizeEnabled)
00142                     {
00143                         this.transform.localPosition = this.m_PositionControl.GetNetworkPosition();
00144                     }
00145 
00146                     if (this.m_RotationModel.SynchronizeEnabled)
00147                     {
00148                         this.transform.localRotation = this.m_RotationControl.GetNetworkRotation();
