00199     public class PhotonTransformViewPositionControl
00200     {
00201         PhotonTransformViewPositionModel m_Model;
00202         float m_CurrentSpeed;
00203         double m_LastSerializeTime;
00204         Vector3 m_SynchronizedSpeed = Vector3.zero;
00205         float m_SynchronizedTurnSpeed = 0;
00206 
00207         Vector3 m_NetworkPosition;
00208         Queue<Vector3> m_OldNetworkPositions = new Queue<Vector3>();
00209 
00210         bool m_UpdatedPositionAfterOnSerialize = true;
00211 
00212         public PhotonTransformViewPositionControl(PhotonTransformViewPositionModel model)
00213         {
00214             m_Model = model;
00215         }
00216 
00217         Vector3 GetOldestStoredNetworkPosition()
00218         {
00219             Vector3 oldPosition = m_NetworkPosition;
00220 
00221             if (m_OldNetworkPositions.Count > 0)
00222             {
00223                 oldPosition = m_OldNetworkPositions.Peek();
00224             }
00225 
00226             return oldPosition;
00227         }
00228 
00237         public void SetSynchronizedValues(Vector3 speed, float turnSpeed)
00238         {
00239             m_SynchronizedSpeed = speed;
00240             m_SynchronizedTurnSpeed = turnSpeed;
00241         }
00242 
00248         public Vector3 UpdatePosition(Vector3 currentPosition)
00249         {
00250             Vector3 targetPosition = GetNetworkPosition() + GetExtrapolatedPositionOffset();
00251 
00252             switch (m_Model.InterpolateOption)
00253             {
00254                 case PhotonTransformViewPositionModel.InterpolateOptions.Disabled:
00255                     if (m_UpdatedPositionAfterOnSerialize == false)
00256                     {
00257                         currentPosition = targetPosition;
00258                         m_UpdatedPositionAfterOnSerialize = true;
00259                     }
00260 
00261                     break;
00262 
00263                 case PhotonTransformViewPositionModel.InterpolateOptions.FixedSpeed:
00264                     currentPosition = Vector3.MoveTowards(currentPosition, targetPosition, Time.deltaTime * m_Model.InterpolateMoveTowardsSpeed);
00265                     break;
00266 
00267                 case PhotonTransformViewPositionModel.InterpolateOptions.EstimatedSpeed:
00268                     if (m_OldNetworkPositions.Count == 0)
00269                     {
00270                         // special case: we have no previous updates in memory, so we can't guess a speed!
00271                         break;
00272                     }
00273 
00274                     // knowing the last (incoming) position and the one before, we can guess a speed.
00275                     // note that the speed is times sendRateOnSerialize! we send X updates/sec, so our estimate has to factor that in.
00276                     float estimatedSpeed = (Vector3.Distance(m_NetworkPosition, GetOldestStoredNetworkPosition()) / m_OldNetworkPositions.Count) * PhotonNetwork.SerializationRate;
00277 
00278                     // move towards the targetPosition (including estimates, if that's active) with the speed calculated from the last updates.
00279                     currentPosition = Vector3.MoveTowards(currentPosition, targetPosition, Time.deltaTime * estimatedSpeed);
00280                     break;
00281 
00282                 case PhotonTransformViewPositionModel.InterpolateOptions.SynchronizeValues:
00283                     if (m_SynchronizedSpeed.magnitude == 0)
00284                     {
00285                         currentPosition = targetPosition;
00286                     }
00287                     else
00288                     {
00289                         currentPosition = Vector3.MoveTowards(currentPosition, targetPosition, Time.deltaTime * m_SynchronizedSpeed.magnitude);
00290                     }
00291 
00292                     break;
00293 
00294                 case PhotonTransformViewPositionModel.InterpolateOptions.Lerp:
00295                     currentPosition = Vector3.Lerp(currentPosition, targetPosition, Time.deltaTime * m_Model.InterpolateLerpSpeed);
00296                     break;
00297             }
00298 
00299             if (m_Model.TeleportEnabled == true)
00300             {
00301                 if (Vector3.Distance(currentPosition, GetNetworkPosition()) > m_Model.TeleportIfDistanceGreaterThan)
00302                 {
00303                     currentPosition = GetNetworkPosition();
00304                 }
00305             }
00306 
00307             return currentPosition;
00308         }
00309 
00314         public Vector3 GetNetworkPosition()
00315         {
00316             return m_NetworkPosition;
00317         }
00318 
00324         public Vector3 GetExtrapolatedPositionOffset()
00325         {
00326             float timePassed = (float)(PhotonNetwork.Time - m_LastSerializeTime);
00327 
00328             if (m_Model.ExtrapolateIncludingRoundTripTime == true)
00329             {
00330                 timePassed += (float)PhotonNetwork.GetPing() / 1000f;
00331             }
00332 
00333             Vector3 extrapolatePosition = Vector3.zero;
00334 
00335             switch (m_Model.ExtrapolateOption)
00336             {
00337                 case PhotonTransformViewPositionModel.ExtrapolateOptions.SynchronizeValues:
00338                     Quaternion turnRotation = Quaternion.Euler(0, m_SynchronizedTurnSpeed * timePassed, 0);
00339                     extrapolatePosition = turnRotation * (m_SynchronizedSpeed * timePassed);
00340                     break;
00341                 case PhotonTransformViewPositionModel.ExtrapolateOptions.FixedSpeed:
00342                     Vector3 moveDirection = (m_NetworkPosition - GetOldestStoredNetworkPosition()).normalized;
00343 
00344                     extrapolatePosition = moveDirection * m_Model.ExtrapolateSpeed * timePassed;
00345                     break;
00346                 case PhotonTransformViewPositionModel.ExtrapolateOptions.EstimateSpeedAndTurn:
00347                     Vector3 moveDelta = (m_NetworkPosition - GetOldestStoredNetworkPosition()) * PhotonNetwork.SerializationRate;
00348                     extrapolatePosition = moveDelta * timePassed;
00349                     break;
00350             }
00351 
00352             return extrapolatePosition;
00353         }
00354 
00355         public void OnPhotonSerializeView(Vector3 currentPosition, PhotonStream stream, PhotonMessageInfo info)
00356         {
00357             if (m_Model.SynchronizeEnabled == false)
00358             {
00359                 return;
00360             }
00361 
00362             if (stream.IsWriting == true)
00363             {
00364                 SerializeData(currentPosition, stream, info);
00365             }
00366             else
00367             {
00368                 DeserializeData(stream, info);
00369             }
00370 
00371             m_LastSerializeTime = PhotonNetwork.Time;
00372             m_UpdatedPositionAfterOnSerialize = false;
00373         }
00374 
00375         void SerializeData(Vector3 currentPosition, PhotonStream stream, PhotonMessageInfo info)
00376         {
00377             stream.SendNext(currentPosition);
00378             m_NetworkPosition = currentPosition;
00379 
00380             if (m_Model.ExtrapolateOption == PhotonTransformViewPositionModel.ExtrapolateOptions.SynchronizeValues ||
00381                 m_Model.InterpolateOption == PhotonTransformViewPositionModel.InterpolateOptions.SynchronizeValues)
00382             {
00383                 stream.SendNext(m_SynchronizedSpeed);
00384                 stream.SendNext(m_SynchronizedTurnSpeed);
00385             }
00386         }
00387 
00388         void DeserializeData(PhotonStream stream, PhotonMessageInfo info)
00389         {
00390             Vector3 readPosition = (Vector3)stream.ReceiveNext();
00391             if (m_Model.ExtrapolateOption == PhotonTransformViewPositionModel.ExtrapolateOptions.SynchronizeValues ||
00392                 m_Model.InterpolateOption == PhotonTransformViewPositionModel.InterpolateOptions.SynchronizeValues)
00393             {
00394                 m_SynchronizedSpeed = (Vector3)stream.ReceiveNext();
00395                 m_SynchronizedTurnSpeed = (float)stream.ReceiveNext();
00396             }
00397 
00398             if (m_OldNetworkPositions.Count == 0)
00399             {
00400                 // if we don't have old positions yet, this is the very first update this client reads. let's use this as current AND old position.
00401                 m_NetworkPosition = readPosition;
00402             }
00403 
00404             // the previously received position becomes the old(er) one and queued. the new one is the m_NetworkPosition
00405             m_OldNetworkPositions.Enqueue(m_NetworkPosition);
00406             m_NetworkPosition = readPosition;
00407 
00408             // reduce items in queue to defined number of stored positions.
00409             while (m_OldNetworkPositions.Count > m_Model.ExtrapolateNumberOfStoredPositions)
00410             {
00411                 m_OldNetworkPositions.Dequeue();
00412             }
00413         }
00414     }
