00458                 default:
00459                 case PhotonTransformViewRotationModel.InterpolateOptions.Disabled:
00460                     return m_NetworkRotation;
00461                 case PhotonTransformViewRotationModel.InterpolateOptions.RotateTowards:
00462                     return Quaternion.RotateTowards(currentRotation, m_NetworkRotation, m_Model.InterpolateRotateTowardsSpeed * Time.deltaTime);
00463                 case PhotonTransformViewRotationModel.InterpolateOptions.Lerp:
00464                     return Quaternion.Lerp(currentRotation, m_NetworkRotation, m_Model.InterpolateLerpSpeed * Time.deltaTime);
00465             }
