00114         private void DrawSynchronizePositionData()
00115         {
00116             if (this.m_SynchronizePositionProperty == null || this.m_SynchronizePositionProperty.boolValue == false)
00117             {
00118                 return;
00119             }
00120 
00121             SerializedProperty interpolatePositionProperty = serializedObject.FindProperty("m_PositionModel.InterpolateOption");
00122             PhotonTransformViewPositionModel.InterpolateOptions interpolateOption = (PhotonTransformViewPositionModel.InterpolateOptions)interpolatePositionProperty.enumValueIndex;
00123 
00124             SerializedProperty extrapolatePositionProperty = serializedObject.FindProperty("m_PositionModel.ExtrapolateOption");
00125             PhotonTransformViewPositionModel.ExtrapolateOptions extrapolateOption = (PhotonTransformViewPositionModel.ExtrapolateOptions)extrapolatePositionProperty.enumValueIndex;
00126 
00127             float containerHeight = 155;
00128 
00129             switch (interpolateOption)
00130             {
00131                 case PhotonTransformViewPositionModel.InterpolateOptions.FixedSpeed:
00132                 case PhotonTransformViewPositionModel.InterpolateOptions.Lerp:
00133                     containerHeight += EDITOR_LINE_HEIGHT;
00134                     break;
00135                 /*case PhotonTransformViewPositionModel.InterpolateOptions.MoveTowardsComplex:
00136                     containerHeight += EDITOR_LINE_HEIGHT*3;
00137                     break;*/
00138             }
00139 
00140             if (extrapolateOption != PhotonTransformViewPositionModel.ExtrapolateOptions.Disabled)
00141             {
00142                 containerHeight += EDITOR_LINE_HEIGHT;
00143             }
00144 
00145             switch (extrapolateOption)
00146             {
00147                 case PhotonTransformViewPositionModel.ExtrapolateOptions.FixedSpeed:
00148                     containerHeight += EDITOR_LINE_HEIGHT;
00149                     break;
00150             }
00151 
00152             if (this.m_InterpolateHelpOpen == true)
00153             {
00154                 containerHeight += GetInterpolateHelpBoxHeight();
00155             }
00156 
00157             if (this.m_ExtrapolateHelpOpen == true)
00158             {
00159                 containerHeight += GetExtrapolateHelpBoxHeight();
00160             }
00161 
00162             // removed Gizmo Options. -3 lines, -1 splitter
00163             containerHeight -= EDITOR_LINE_HEIGHT * 3;
00164 
00165             Rect rect = PhotonGUI.ContainerBody(containerHeight);
00166 
00167             Rect propertyRect = new Rect(rect.xMin + 5, rect.yMin + 2, rect.width - 10, EditorGUIUtility.singleLineHeight);
00168 
00169             DrawTeleport(ref propertyRect);
00170             DrawSplitter(ref propertyRect);
00171 
00172             DrawSynchronizePositionDataInterpolation(ref propertyRect, interpolatePositionProperty, interpolateOption);
00173             DrawSplitter(ref propertyRect);
00174 
00175             DrawSynchronizePositionDataExtrapolation(ref propertyRect, extrapolatePositionProperty, extrapolateOption);
00176         }
