00214         private void DrawPropertyWithHelpIcon(ref Rect propertyRect, ref bool isHelpOpen, SerializedProperty property, string tooltip)
00215         {
00216             Rect propertyFieldRect = new Rect(propertyRect.xMin, propertyRect.yMin, propertyRect.width - 20, propertyRect.height);
00217             string propertyName = ObjectNames.NicifyVariableName(property.name);
00218             EditorGUI.PropertyField(propertyFieldRect, property, new GUIContent(propertyName, tooltip));
00219 
00220             Rect helpIconRect = new Rect(propertyFieldRect.xMax + 5, propertyFieldRect.yMin, 20, propertyFieldRect.height);
00221             isHelpOpen = GUI.Toggle(helpIconRect, isHelpOpen, PhotonGUI.HelpIcon, GUIStyle.none);
00222 
00223             propertyRect.y += EDITOR_LINE_HEIGHT;
00224         }
