00396         private void DrawHeader(string label, SerializedProperty property)
00397         {
00398             if (property == null)
00399             {
00400                 return;
00401             }
00402 
00403             bool newValue = PhotonGUI.ContainerHeaderToggle(label, property.boolValue);
00404 
00405             if (newValue != property.boolValue)
00406             {
00407                 property.boolValue = newValue;
00408                 property.serializedObject.ApplyModifiedProperties();
00409             }
00410         }
