00221 
00222                 this.Owner = PhotonNetwork.CurrentRoom == null ? null : PhotonNetwork.CurrentRoom.GetPlayer(value, true);
00223                 this.ownerActorNr = this.Owner != null ? this.Owner.ActorNumber : value;
00224 
00225                 this.AmOwner = PhotonNetwork.LocalPlayer != null && this.ownerActorNr == PhotonNetwork.LocalPlayer.ActorNumber;
00226 
00227                 this.UpdateCallbackLists();
