00229                 {
00230                     for (int i = 0, cnt = this.OnOwnerChangeCallbacks.Count; i < cnt; ++i)
00231                     {
00232                         this.OnOwnerChangeCallbacks[i].OnOwnerChange(this.Owner, prevOwner);
