00288         public int ViewID
00289         {
00290             get
00291             {
00292                 return this.viewIdField;
00293             }
00294 
00295             set
00296             {
00297                 // TODO: Check if the isPlaying check is needed when the PhotonViewHandler is updated
00298                 if (value != 0 && this.viewIdField != 0)
00299                 {
00300                     Debug.LogWarning("Changing a ViewID while it's in use is not possible (except setting it to 0 (not being used). Current ViewID: " + this.viewIdField);
00301                     return;
00302                 }
00303                 
00304                 if (value == 0 && this.viewIdField != 0)
00305                 {
00306                     PhotonNetwork.LocalCleanPhotonView(this);
00307                 }
00308 
00309                 this.viewIdField = value;
00310                 this.CreatorActorNr = value / PhotonNetwork.MAX_VIEW_IDS;   // the creator can be derived from the viewId. this is also the initial owner and creator.
00311                 this.OwnerActorNr = this.CreatorActorNr;
00312                 this.ControllerActorNr = this.CreatorActorNr;
00313                 this.RebuildControllerCache();
00314 
00315 
00316                 // if the viewID is set to a new, legit value, the view should register in the list of active PVs.
00317                 if (value != 0)
00318                 {
00319                     PhotonNetwork.RegisterPhotonView(this);
00320                 }
00321             }
